import React, { useState, useEffect } from 'react'

export default function FormOfProduct() {
    const [productName, setProductName] = useState('1')
    const [price, setPrice] = useState('2')

    const handleChange = (event) => {
        const { name, value } = event.target
        switch (name) {
            case 'productName':
                setProductName(value)
                break

            case 'price':
                setPrice(value)
                if (value.length < 6) {
                    alert('мало символів')
                } else break
                break
            default:
                return
        }
    }
    useEffect(() => {})
    return (
        <form>
            <label>
                <span>Name of product</span>
                <input
                    type="text"
                    name="productName"
                    onChange={handleChange}
                    value={productName}
                    // onBlur={}
                    // onFocus={}
                ></input>
            </label>

            <label>
                <span>Price of product</span>
                <input
                    name="price"
                    type="text"
                    value={price}
                    onChange={handleChange}
                ></input>
            </label>
            <button type="submit">Add product</button>
        </form>
    )
}
