import React, { useState} from 'react';
// import { v4 as uuidv4 } from 'uuid';
// import axios from 'axios';

export default function FormOfProduct() {
    const [product, setProduct] = useState({
        name: '',
        price: ''
    });
    ////////////////////////////////////
    // const [file, setFile] = useState(null)
    // const selectFile = async (event) =>{
    //     const files = event.target.files
    //     setFile(files[0]);
        // const formData = new FormData()
        // formData.append('img', files[0])
        // formData.append('product', product)
        // const url = 'http://localhost:8080/api/products/form' ;

    //     fetch(url,{ method: 'POST', body:formData })
    // .then(res=>res.json())
    // .then(data=>{
    //     console.log(data);
    // })
    // .catch((error)=>{console.log(error)})
    // }


//////////////////////////////////////
    const handleChange = (event) => {
        const { name, value} = event.target
        setProduct({ ...product, [name]: value })
    }
    const handleSubmit = (event) => {
        if (product.name === '' || !product.price) {
            alert('Write all fields!')
       } else
       {
            event.preventDefault();
        createNewProduct(product);
 }
    };

    const createNewProduct = async () => {
        const url = 'http://localhost:8080/api/products/form' ;
        const fetchData = {
            method: 'POST',
            body: product,
            headers: {
                'Content-Type': 'application/json'
              },
        };
        console.log("fetchData--",fetchData)
            const request = async () => {
            const res = await fetch(url, fetchData);
            const data = await res.json();
            if (!res.ok) {
                throw new Error(data.message || "Something was wrong")
            }
              return alert(data)
            };

        try {
            if (product) {
                request();
            }
        } 
        catch (error) {
             return error.message
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <span>Name of product</span>
                <input
                    type="text"
                    name="name"
                    onChange={handleChange}
                ></input>
            </label>
            <label>
                <span>Price of product</span>
                <input name="price" type="text" onChange={handleChange}></input>
            </label>
            {/* ///////////////////// */}
            {/* <label> 
                <br/>
                <input  type="file" 
                name="img"
                onChange={selectFile}></input>
            </label> */}
           {/* /////////////////////*/}
        </form>
    )
}
