import React, { useState } from 'react'

export default function FormOfProduct() {
    const [product, setProduct] = useState({
        productName: '',
        price: '',
    })
    const handleChange = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name
        setProduct({ ...product, [name]: value })
    }
    const handleSubmit = (event) => {
        if (product.productName === '' || !product.price) {
            alert('Write all fields!')
            return
        } else event.preventDefault()
        console.log(product)

        const url = 'http://localhost:8080/api/products';
        const sendNewProduct = {
            method: 'POST',
            body: product,
            headers: new Headers()
          };
        fetch(url, sendNewProduct)
        console.log('sending form')
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <span>Name of product</span>
                <input
                    type="text"
                    name="productName"
                    onChange={handleChange}
                ></input>
            </label>

            <label>
                <span>Price of product</span>
                <input name="price" type="text" onChange={handleChange}></input>
            </label>

            <button type="submit">Add product</button>
        </form>
    )
}
