import React from "react";
import {useParams} from 'react-router-dom';
import ProductArray from "../ProductArray/ProductArray";

export default function ProductDescription({ProductArray}){
    const {array}= ProductArray;
    const {productId} = useParams();
   
    return(
     <div>
         {array.map(product=><h3 key={product.id}>
            {productId===product.id ? `Product ${product.name} price: ${product.price}`: null}</h3>)}
                     {/* {products.find(product=>
                     <h3 key={product.id}> {{productId}===product.id `Product ${product.name} price: ${product.price}`}</h3>)} */}

               </div>   
    )
}
