import {useState, useEffect} from 'react'

const useRequest = (url, fetchData) => {
  const [data, setData] = useState([]);

  useEffect(() => {
      if (!fetchData) return;
      const request = async () => {
          const response = await fetch(url, fetchData);
          const data = await response.json();
          setData(data);
      };

      request();
  }, [url]);

  return { data };
};
export default useRequest;
