import React from "react";
import {useParams} from 'react-router-dom';
import ProductArray from "../mocks/ProductArray";

export default function ProductDescription(){
    const {productId} = useParams();
   
    return(
     <div>
         {ProductArray.map(product=><h3 key={product.id}>
            {productId===product.id ? `Product ${product.name} price: ${product.price}`: null}</h3>)}
                     {/* {products.find(product=>
                     <h3 key={product.id}> {{productId}===product.id `Product ${product.name} price: ${product.price}`}</h3>)} */}

               </div>   
    )
}
