import React, { useState} from 'react'
// import { useMessage } from '../hooks/message';

export default function FormOfProduct() {
    const [product, setProduct] = useState({
        name: '',
        price: '',
    });
    // const message = useMessage();
    // const [error, setError]= useState(null);
    // const [loading, setLoading] =useState(false);

//     useEffect(()=>{
// message(error)
//     }, [error, message])

    const handleChange = (event) => {
        const { name, value } = event.target
        setProduct({ ...product, [name]: value })
    }
    const handleSubmit = (event) => {
    //     if (product.name === '' || !product.price) {
    //         alert('Write all fields!')
    //    } else
    //    {
            event.preventDefault();
        createNewProduct(product);
// }
    };

    const createNewProduct = async () => {
        const url = 'http://localhost:8080/api/products/form' ;
        const fetchData = {
            method: 'POST',
            body: JSON.stringify(product),
            headers: {
                'Content-Type': 'application/json'
              },
        };
        const request = async () => {
            // setLoading(true);
            const res = await fetch(url, fetchData);
            const data = await res.json();
            if (!res.ok) {
                throw new Error(data.message || "Something was wrong")
            }
            // setLoading(false)
              return data
            };

        try {
            if (product) {
                request();
            }
        } 
        catch (error) {
             return alert(error.message)
        }
        // const clearError = ()=> setError(null)
        // return {loading, request, error, clearError}
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <span>Name of product</span>
                <input
                    type="text"
                    name="name"
                    onChange={handleChange}
                ></input>
            </label>
            <label>
                <span>Price of product</span>
                <input name="price" type="text" onChange={handleChange}></input>
            </label>
            <button type="submit">Add product</button>
        </form>
    )
}
