import React, { useEffect, useState } from "react";
import {useParams} from 'react-router-dom';

export default function ProductDescription(){
    const [product, setProductArray] = useState();
    const {productId} = useParams();

useEffect(()=>{
    async function fetchSubmit(){
        const rv =await(await fetch("http://localhost:8080/api/users")).json();
        const product = rv.find(product=>productId===product?.id);
        setProductArray(product);
        }
fetchSubmit();
    }, []);
     return(
         <h3 key={product.id}> { `Product ${product.name} price: ${product.price}`}</h3>              
    )}
