import { useParams } from "react-router-dom";
import { Link, Card, CardContent, CardMedia } from "@mui/material";

export default function ProductDescription() {
  const [product, setProduct] = useState({});
  const { productId } = useParams();

  useEffect(() => {
    async function fetchSubmit() {
      const product = await (
        await fetch(`http://localhost:8080/api/products/${productId}`)
      ).json();
      // const product = productResponse?.find(product=>productId===product?.id);
      setProduct(product);
    }
    fetchSubmit();
  }, []);
  return (

    <div key={product.id}>
      <Card
        sx={{
          textAlign: "center",
          display: "flex",
          flexWrap: "wrap",
          gap: "30px",
          "& > :not(style)": {
            m: 1,
            width: 70,
          },
        }}
      >

          <CardContent >
          <img src= {`http://localhost:8080/${product.img}`} 
      alt ="imageOfProduct"
      height="40" weight="40"/>
            <CardMedia
              // component="img"
