import {useState, useEffect} from 'react'

const useRequest =  (url, fetchData) => {
    const [state, setState] = useState(undefined)

 function fetchNow(url, fetchData){
    const res =  fetch(url, fetchData);
    const data =  res.json();
    if (!res.ok) {
      throw new Error(data.message || "Something was wrong");
    } else {
      return (
        setState(data)
      )
    //   alert(data);
    }
}
